import React, { useEffect, useState } from "react";
import axios from "axios";
import "../Style/homeStyle.css";

const Header = () => {
  const handleLogout = () => {
    // Perform logout logic here
    // For example, clear the token from localStorage or perform any other necessary actions
    localStorage.removeItem("token");
    // Redirect the user to the login page or any other desired page
    window.location.href = "/";
  };
  return (
    <header>
      <div className="logo">Logo</div>
    </header>
  );
};

const Section = ({ id, title, description }) => {
  return (
    <section id={id} className="section">
      <div className="section-content">
        <h2>{title}</h2>
        <p>{description}</p>
        <a href={`/${id}`} className="button">
          Više informacija
        </a>
      </div>
    </section>
  );
};

const Footer = () => {
  return (
    <footer>
      <p>© 2023 TabTracker. Sva prava pridržana.</p>
      <p>
        <a href="#">Uvjeti korištenja</a> | <a href="#">Pravila privatnosti</a>{" "}
        | <a href="#">Kontakt</a> | <a href="#">O nama</a>
      </p>
    </footer>
  );
};

const Home = () => {
  const [sections, setSections] = useState([
    {
      id: "favorite",
      title: "Omiljeni tabovi",
      description: "Opis omiljenih tabova",
    },
    {
      id: "addtab",
      title: "Dodaj Tab",
      description: "Opis omiljenih tabova",
    },
    {
      id: "archive",
      title: "Arhiva tabova",
      description: "Opis arhive tabova",
    },
    {
      id: "profile",
      title: "Profil",
      description: "Opis profila",
    },
    {
      id: "settings",
      title: "Postavke",
      description: "Opis postavki",
    },
    {
      id: "news",
      title: "Novosti",
      description: "Opis novosti",
    },
  ]);

  return (
    <div className="Home">
      <Header />
      {sections.map((section) => (
        <Section
          key={section.id}
          id={section.id}
          title={section.title}
          description={section.description}
        />
      ))}
      <Footer />
    </div>
  );
};

export default Home;
