import React, { useState, useEffect } from "react";
import "../Style/addTabStyle.css";
import axios from "axios";
import Header from "../components/Navigation";

const AddSong = () => {
  const [title, setTitle] = useState("");
  const [artist, setArtist] = useState("");
  const [album, setAlbum] = useState("");
  const [chords, setChords] = useState("");
  const [lyrics, setLyrics] = useState("");
  const [favorite, setFavorite] = useState(false);
  const [albumPicture, setAlbumPicture] = useState("");
  const [ytLink, setYtLink] = useState("");
  const [loggedInUserID, setLoggedInUserID] = useState("");

  useEffect(() => {
    const fetchLoggedInUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          throw new Error("No token found");
        }

        const response = await axios.get(
          "http://localhost:8000/user/getLoggedInUser",
          {
            headers: {
              Authorization: token,
            },
          }
        );
        console.log(response.data); // Check the response data
        const userID = response.data;
        const userIDStr = userID.toString();
        console.log("aaaaaaaaaaaaaaaaaaaaaaaa");
        console.log(userIDStr); // Check the retrieved userID
        setLoggedInUserID(userIDStr);
      } catch (error) {
        console.log(error);
      }
    };

    fetchLoggedInUser();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create an object with form data
    const songData = {
      title: title,
      artist: artist,
      album: album,
      chords: chords,
      lyrics: lyrics,
      favorite: favorite ? loggedInUserID : "", // Update the favorite field
      albumPicture: albumPicture,
      ytLink: ytLink,
    };

    // Send a POST request to the backend
    axios
      .post("http://localhost:8000/song/songs", songData)
      .then((response) => {
        // Successfully saved the song on the backend
        console.log(response.data);

        // Reset the form
        setTitle("");
        setArtist("");
        setAlbum("");
        setChords("");
        setLyrics("");
        setFavorite(false);
        setAlbumPicture("");
        setYtLink("");
      })
      .catch((error) => {
        // Error while sending the request to the backend
        console.error(error);
      });
  };

  return (
    <div>
      <Header />
      <div className="add-song-container">
        <h1>Add Song</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="artist">Artist:</label>
            <input
              type="text"
              id="artist"
              value={artist}
              onChange={(e) => setArtist(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="album">Album:</label>
            <input
              type="text"
              id="album"
              value={album}
              onChange={(e) => setAlbum(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="chords">Chords:</label>
            <textarea
              id="chords"
              value={chords}
              onChange={(e) => setChords(e.target.value)}
              required
            ></textarea>
          </div>

          <div className="form-group">
            <label htmlFor="lyrics">Lyrics:</label>
            <textarea
              id="lyrics"
              value={lyrics}
              onChange={(e) => setLyrics(e.target.value)}
              required
            ></textarea>
          </div>

          <div className="form-group">
            <label htmlFor="favorite">Is Favorite:</label>
            <select
              id="favorite"
              value={favorite ? "1" : "0"}
              onChange={(e) => setFavorite(e.target.value === "1")}
              required
            >
              <option value="0">No</option>
              <option value="1">Yes</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="albumPicture">Album Image Link:</label>
            <input
              type="text"
              id="albumPicture"
              value={albumPicture}
              onChange={(e) => setAlbumPicture(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="ytLink">YouTube Link:</label>
            <input
              type="text"
              id="ytLink"
              value={ytLink}
              onChange={(e) => setYtLink(e.target.value)}
              required
            />
          </div>

          <button type="submit">Add Song</button>
        </form>
      </div>
    </div>
  );
};

export default AddSong;
