import React, { useState, useEffect } from "react";
import axios from "axios";
import "../Style/favoriteStyle.css";
import Header from "../components/Navigation";

const FavoriteSongs = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [songs, setSongs] = useState([]);
  const [albums, setAlbums] = useState([]);
  const [artists, setArtists] = useState([]);

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        const response = await axios.get("http://localhost:8000/song/songs");
        setSongs(response.data);
        setAlbums([...new Set(response.data.map((song) => song.album))]);
        setArtists([...new Set(response.data.map((song) => song.artist))]);
      } catch (error) {
        console.log(error);
        alert("Failed to fetch songs");
      }
    };

    fetchSongs();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredSongs = songs.filter(
    (song) =>
      song.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      song.artist.toLowerCase().includes(searchTerm.toLowerCase()) ||
      song.album.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <Header />
      <div className="favorite-songs">
        <h1>Songs</h1>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search songs"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <select
            value={searchTerm}
            onChange={handleSearchChange}
            className="filter-dropdown"
          >
            <option value="">All</option>
            {albums.map((album, index) => (
              <option key={index} value={album}>
                Album: {album}
              </option>
            ))}
            {artists.map((artist, index) => (
              <option key={index} value={artist}>
                Artist: {artist}
              </option>
            ))}
          </select>
        </div>

        {filteredSongs.length > 0 ? (
          <div className="tab-content">
            {filteredSongs.map((song, index) => (
              <div key={index} className="song-details">
                {/* ... */}
              </div>
            ))}
          </div>
        ) : (
          <p className="no-results">No songs found.</p>
        )}
      </div>
    </div>
  );
};

export default FavoriteSongs;
