import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";
import "../Style/Profile.css";
import Header from "../components/Navigation";

const ProfileContext = createContext();

export const useProfile = () => {
  return useContext(ProfileContext);
};

const ProfileProvider = ({ children }) => {
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        // Fetch the profile data for the logged-in user
        const token = localStorage.getItem("token"); // Get the JWT token from localStorage or wherever it is stored
        if (!token) {
          throw new Error("No token found");
        }

        const response = await axios.get(
          "http://localhost:8000/user/getLoggedInUser",
          {
            headers: {
              Authorization: token,
            },
          }
        );

        const loggedInUserID = response.data.userId; // Access the userId property from the response data

        console.log(loggedInUserID);

        // Use the user ID to fetch the profile data
        const profileResponse = await axios.get(
          `http://localhost:8000/user/getuser/${loggedInUserID}`
        );

        setProfileData(profileResponse.data);
      } catch (error) {
        console.log(error);
        // Add error handling logic here
      }
    };

    fetchProfileData();
  }, []);

  if (!profileData) {
    // Add loading indicators or other logic for loading state
    return null;
  }

  return (
    <ProfileContext.Provider value={profileData}>
      <div className="profile-container">
        <Header />
        <div className="profile-header">
          <h1>Profilna Stranica</h1>
        </div>
        <div className="profile-content">
          <div className="profile-info">
            <h2>Informacije o korisniku</h2>
            <p>
              Ime: {profileData.firstName} {profileData.lastName}
            </p>
            <p>Email: {profileData.email}</p>
            {/* Display other user details */}
          </div>
          <div className="profile-avatar">
            <img src={profileData.avatar} alt="Profilna slika" />
          </div>
        </div>
      </div>
    </ProfileContext.Provider>
  );
};

export default ProfileProvider;
