import React, { useState, useEffect, createContext } from "react";
import axios from "axios";
import "../Style/favoriteStyle.css";

const FavoriteSongs = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const [songs, setSongs] = useState([]);

  const handleTabClick = (tabId) => {
    setActiveTab(tabId);
  };

  useEffect(() => {
    // Funkcija za dohvaćanje pjesama iz baze podataka
    const fetchSongs = async () => {
      try {
        const response = await axios.get("http://localhost:8000/song/songs");
        setSongs(response.data);
      } catch (error) {
        console.log(error);
        alert("Failed to fetch songs");
      }
    };

    fetchSongs();
  }, []);

  return (
    <div className="favorite-songs">
      <h1>Favorite Songs</h1>

      <div className="tabs">
        {songs.map((song, index) => (
          <div
            key={index}
            className={`tab ${activeTab === `tab${index + 1}` ? "active" : ""}`}
            onClick={() => handleTabClick(`tab${index + 1}`)}
          >
            {song.title}
          </div>
        ))}
      </div>

      <div className="tab-content">
        {songs.map((song, index) => (
          <div
            key={index}
            id={`tab${index + 1}`}
            className={`tab-pane ${
              activeTab === `tab${index + 1}` ? "active" : ""
            }`}
          >
            <h2>{song.title}</h2>
            <p>Artist: {song.artist}</p>
            <p>Album: {song.album}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FavoriteSongs;
