import React, { useState } from "react";
import "./login.css";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ovde možeš implementirati poziv ka backendu sa prosleđenim korisničkim podacima
    // Na primer, možeš koristiti fetch ili axios biblioteku za slanje zahteva

    // Dummy provera korisničkog imena i lozinke
    if (username === "admin" && password === "password") {
      // Uspešna prijava
      setErrorMessage("");
      alert("Uspesna prijava!");
    } else {
      // Neuspešna prijava
      setErrorMessage("Pogresno korisnicko ime ili lozinka");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Korisnicko ime:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div>
          <label htmlFor="password">Lozinka:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <button type="submit">Prijavi se</button>
        {errorMessage && <p>{errorMessage}</p>}
      </form>
    </div>
  );
}

export default Login;
