import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";
import "../Style/Profile.css";

const ProfileContext = createContext();

export const useProfile = () => {
  return useContext(ProfileContext);
};

const ProfileProvider = ({ children }) => {
  const [profileData, setProfileData] = useState(null);
  const [userID, setUserID] = useState(null); // State for storing the user ID

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        // Fetch the user ID from your authentication system or wherever it is stored
        const loggedInUserID = fetchLoggedInUserID(); // Replace this with your actual implementation

        // Set the user ID in the state
        setUserID(loggedInUserID);

        // Fetch the profile data using the user ID
        const response = await axios.get(
          `http://localhost:8000/getuser/user/${loggedInUserID}`
        );
        setProfileData(response.data);
      } catch (error) {
        console.log(error);
        // Add error handling logic here
      }
    };

    fetchProfileData();
  }, []);

  if (!profileData) {
    // Add loading indicators or other logic for loading state
    return null;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>Profilna Stranica</h1>
      </div>
      <div className="profile-content">
        <div className="profile-info">
          <h2>Informacije o korisniku</h2>
          <p>
            Ime: {profileData.firstName} {profileData.lastName}
          </p>
          <p>Email: {profileData.email}</p>
          <p>Godine: {profileData.age}</p>
          <p>Adresa: {profileData.address}</p>
        </div>
        <div className="profile-avatar">
          <img src={profileData.avatar} alt="Profilna slika" />
        </div>
      </div>
    </div>
  );
};

export default ProfileProvider;
