import React, { useEffect, useState } from "react";
import axios from "axios";
import "../Style/homeStyle.css";

const Header = () => {
  return (
    <header>
      <div className="logo">Logo</div>
      <nav>
        <ul>
          <li>
            <a href="#home">Početna</a>
          </li>
          <li>
            <a href="#favorite">Omiljeni tabovi</a>
          </li>
          <li>
            <a href="#archive">Arhiva tabova</a>
          </li>
          <li>
            <a href="#profile">Profil</a>
          </li>
          <li>
            <a href="#settings">Postavke</a>
          </li>
          <li>
            <a href="#news">Novosti</a>
          </li>
        </ul>
      </nav>
    </header>
  );
};

const Section = ({ id, title, description }) => {
  return (
    <section id={id} className="section">
      <div className="section-content">
        <h2>{title}</h2>
        <p>{description}</p>
        <a href={`#${id}`} className="button">
          Više informacija
        </a>
      </div>
    </section>
  );
};

const Home = () => {
  const [sections, setSections] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://example.com/api/sections" // Zamijenite sa stvarnim endpointom za dohvaćanje podataka
        );
        setSections(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="Home">
      <Header />
      {sections.map((section) => (
        <Section
          key={section.id}
          id={section.id}
          title={section.title}
          description={section.description}
        />
      ))}
      <Footer />
    </div>
  );
};

export default Home;
