import React, { useState, useEffect } from "react";
import axios from "axios";
import "../Style/favoriteStyle.css";
import Header from "../components/Navigation";

const FavoriteSongs = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        const response = await axios.get("http://localhost:8000/song/songs");
        setSongs(response.data);
      } catch (error) {
        console.log(error);
        alert("Failed to fetch songs");
      }
    };

    fetchSongs();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredSongs = songs.filter((song) =>
    song.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <Header />
      <div className="favorite-songs">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search songs"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>

        <div className="tab-content">
          {filteredSongs.map((song, index) => (
            <div key={index} className="tab-pane active">
              <div className="song-details">
                <div className="media-container">
                  <img
                    src={song.albumPicture}
                    alt={song.title}
                    className="album-image"
                  />
                  <div className="beneathPicture">
                    <div className="song-details-content">
                      <h2 className="song-title">{song.title}</h2>
                      <p className="artist-album">Artist: {song.artist}</p>
                      <p className="artist-album">Album: {song.album}</p>
                      <a
                        href={`https://www.youtube.com/watch?v=${song.ytLink}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="youtube-link"
                      >
                        Watch on YouTube
                      </a>
                    </div>
                  </div>
                </div>
                <div className="chords-section">
                  <h3>Chords</h3>
                  <pre>{song.chords}</pre>
                </div>
                <div className="lyrics-section">
                  <h3>Lyrics</h3>
                  <pre>{song.lyrics}</pre>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FavoriteSongs;
