import React, { useEffect, useState } from "react";

function App() {
  const [backendData, setBackendData] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    fetch("/user/api")
      .then((response) => response.json())
      .then((data) => {
        setBackendData(data);
      })
      .catch((error) => {
        console.error("Error fetching backend data:", error);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Provjerite valjanost podataka
    if (!name || !email || !password) {
      console.log("Molimo ispunite sva polja");
      return;
    }

    // Kreirajte novog korisnika
    const newUser = {
      name: name,
      email: email,
      password: password,
    };

    // Dodajte novog korisnika u listu registriranih korisnika
    setBackendData([...backendData, newUser]);

    // Resetiranje polja za unos nakon registracije
    setName("");
    setEmail("");
    setPassword("");
  };

  return (
    <div>
      <h1>Registracija</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Ime:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label>
          Lozinka:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <button type="submit">Registriraj se</button>
      </form>

      <div>
        {backendData.length === 0 ? (
          <p>Nema registriranih korisnika</p>
        ) : (
          backendData.map((user, i) => (
            <p key={i}>
              {user.firstName} {user.lastName}
              {user.password} {user.email}
            </p>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
