import React, { useState, useEffect, createContext } from "react";
import axios from "axios";
import "../Style/favoriteStyle.css";

const FavoriteSongs = () => {
  const [activeTab, setActiveTab] = useState("");
  const [songs, setSongs] = useState([]);

  const handleTabClick = (tabId) => {
    setActiveTab(tabId);
  };

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        const response = await axios.get("http://localhost:8000/song/songs");
        setSongs(response.data);
        setActiveTab(response.data[0].id); // Postavljamo aktivnu karticu na prvu pjesmu
      } catch (error) {
        console.log(error);
        alert("Failed to fetch songs");
      }
    };

    fetchSongs();
  }, []);

  const getSongById = (id) => {
    return songs.find((song) => song.id === id);
  };

  return (
    <div className="favorite-songs">
      <h1>Favorite Songs</h1>

      <div className="tabs">
        {songs.map((song) => (
          <div
            key={song.id}
            className={`tab ${activeTab === song.id ? "active" : ""}`}
            onClick={() => handleTabClick(song.id)}
          >
            {song.title}
          </div>
        ))}
      </div>

      <div className="tab-content">
        {songs.map((song) => (
          <div
            key={song.id}
            id={song.id}
            className={`tab-pane ${activeTab === song.id ? "active" : ""}`}
          >
            <h2>{song.title}</h2>
            <p>Artist: {song.artist}</p>
            <p>Album: {song.album}</p>
            <div className="chords-section">
              <h3>Chords</h3>
              <p>{song.chords}</p>
            </div>
            <div className="lyrics-section">
              <h3>Lyrics</h3>
              <p>{song.lyrics}</p>
            </div>
            <p>Notes: {song.notes}</p>
            <img src={song.albumPicture} alt={song.title} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default FavoriteSongs;
