import React, { useState } from "react";
import "../Style/favoriteStyle.css";

const FavoriteSongs = () => {
  const [activeTab, setActiveTab] = useState("tab1");

  const handleTabClick = (tabId) => {
    setActiveTab(tabId);
  };

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/song/songs", {
        title,
        artist,
        album,
        chords,
        lyrics,
        notes,
        albumPicture,
      });
    } catch (error) {
      console.log(error);
      alert("Wrong details");
    }
  }

  return (
    <div className="favorite-songs">
      <h1>Favorite Songs</h1>

      <div className="tabs">
        <div
          className={`tab ${activeTab === "tab1" ? "active" : ""}`}
          onClick={() => handleTabClick("tab1")}
        >
          Song 1
        </div>
        <div
          className={`tab ${activeTab === "tab2" ? "active" : ""}`}
          onClick={() => handleTabClick("tab2")}
        >
          Song 2
        </div>
        <div
          className={`tab ${activeTab === "tab3" ? "active" : ""}`}
          onClick={() => handleTabClick("tab3")}
        >
          Song 3
        </div>
      </div>

      <div className="tab-content">
        <div
          id="tab1"
          className={`tab-pane ${activeTab === "tab1" ? "active" : ""}`}
        >
          <h2>Song 1</h2>
          <p>Artist: Artist 1</p>
          <p>Album: Album 1</p>
        </div>
        <div
          id="tab2"
          className={`tab-pane ${activeTab === "tab2" ? "active" : ""}`}
        >
          <h2>Song 2</h2>
          <p>Artist: Artist 2</p>
          <p>Album: Album 2</p>
        </div>
        <div
          id="tab3"
          className={`tab-pane ${activeTab === "tab3" ? "active" : ""}`}
        >
          <h2>Song 3</h2>
          <p>Artist: Artist 3</p>
          <p>Album: Album 3</p>
        </div>
      </div>
    </div>
  );
};

export default FavoriteSongs;
